package sg.edu.nus.mda.simquery.preprocess.pivotselection;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.BufferedWriter;
import java.io.InputStreamReader;
import java.io.OutputStreamWriter;
import java.util.HashMap;
import java.util.Map;
import java.util.Random;

import org.apache.hadoop.conf.Configuration;
import org.apache.hadoop.fs.FSDataInputStream;
import org.apache.hadoop.fs.FSDataOutputStream;
import org.apache.hadoop.fs.FileSystem;
import org.apache.hadoop.fs.Path;
import org.apache.hadoop.util.GenericOptionsParser;

import sg.edu.nus.mda.simquery.metricspace.IMetric;
import sg.edu.nus.mda.simquery.metricspace.IMetricSpace;
import sg.edu.nus.mda.simquery.metricspace.MetricFactory;
import sg.edu.nus.mda.simquery.metricspace.MetricSpaceFactory;
import sg.edu.nus.mda.simquery.preprocess.SQConfig;

/**
 * We use it to select pivots according to the selection strategies: (1) random (2) farthest (3) greedy (4) kmean
 * 
 * 
 * @author luwei
 *
 */
public class PivotSelectionFactory extends APivotSelection {
	int sampleSize = 10000;
	Object[] sample = null;
	int dim;
	int numOfPivots;
	int numOfPivotSets = 1;
	IMetricSpace metricSpace;

	/**
	 * 
	 * @param ms
	 *            : metric space to be operated
	 * @param m
	 *            : metric or distance function
	 * @param pivot
	 *            : number of pivots
	 * @param dim
	 *            : dimension; set to 1 for text space
	 */
	public PivotSelectionFactory(IMetricSpace ms, int numOfPivots, int dim) {
		metricSpace = ms;
		this.numOfPivots = numOfPivots;
		this.dim = dim;
	}

	/**
	 * @param 
	 */
	public static void main(String[] args) {
		try {
			long begin = System.currentTimeMillis();
			pivotSelectionMain(args);
			long end = System.currentTimeMillis();
			long second = (end - begin) / 1000;
			System.err.println("PivotSelection takes " + second + " seconds");
		} catch (Exception ex) {
			ex.printStackTrace();
		}
	}

	/**
	 * We read lines from the source files according to the line ids that are
	 * maintained in Map readLineIDs with format <lineId, (pivotSetID, offset)>
	 * 
	 * @param path
	 *            : source file path dir
	 * @param readLineIDs
	 *            : line ids to be read
	 */
	private void read(String input, Map<Integer, Long> readLineIDs)
			throws IOException {
		if (readLineIDs == null || readLineIDs.size() != sampleSize)
			return;

		Configuration conf = new Configuration();
	    conf.addResource(new Path(SQConfig.configPath1));
	    conf.addResource(new Path(SQConfig.configPath2));
	    conf.addResource(new Path(SQConfig.configPath3));
	    
		FileSystem fs = FileSystem.get(conf);
		FSDataInputStream inputStream = fs.open(new Path(input));
		BufferedReader br;
		br = new BufferedReader(new InputStreamReader(inputStream));
		String tmp;
		int lineIDOfFile = 0;
		int offsetOfSample;

		while ((tmp = br.readLine()) != null) {
			if (readLineIDs.containsKey(lineIDOfFile)) {
				long value = readLineIDs.get(lineIDOfFile);
				int offset = (int) (value & 0x00000000ffffffff);
				int pivotSetID = (int) (value >> 32);

				offsetOfSample = pivotSetID * numOfPivots + offset;
				sample[offsetOfSample] = metricSpace.readObject(tmp, dim);
			}
			lineIDOfFile++;
		}

		br.close();
		inputStream.close();
		fs.close();
	}

	@Override
	public int read(String dir, int numOfObjects, int totNumOfObjects) {
		/**
		 * ======begin to generate a set of random ids as the
		 * samples================
		 */
		sampleSize = numOfObjects;
		sample = new Object[sampleSize];
		numOfPivotSets = numOfObjects / numOfPivots;

		int i, j;
		int lineID;
		long value;
		/**
		 * Map randLineIDs is used to map line ids to position in the sampling
		 * array
		 */
		Map<Integer, Long> randLineIDs = new HashMap<Integer, Long>(sampleSize);
		Random randomGenerator = new Random(numOfObjects);

		for (i = 0; i < numOfPivotSets; i++) {
			for (j = 0; j < numOfPivots; j++) {
				while (true) {
					/** duplicates are not allowed in our case */
					lineID = randomGenerator.nextInt(totNumOfObjects);
					if (!randLineIDs.containsKey(lineID)) {
						value = i;
						value = (value << 32) + j;
						randLineIDs.put(lineID, value);
						break;
					}
				}
			}
		}
		/**
		 * ======end to generate a set of random ids as the
		 * samples================
		 */
		try {
			read(dir, randLineIDs);
			return 1;
		} catch (Exception ex) {
			ex.printStackTrace();
			return -1;
		}
	}

	@Override
	public void selectPivots(SelStrategy sg, IMetric metric) throws IOException {
		if (sg == SelStrategy.random) {
			randomSelect(metric);
		} else if (sg == SelStrategy.furthest) {
			furthestSelect(metric);
		} else if (sg == SelStrategy.greedy) {
			greedySelect(metric);
		} else {
			System.out.println("Error: We do not support " + sg + " yet!");
		}
		System.out.println("===========pivots selection ends.=============");
	}

	private double getSumDistFromPivotSet(IMetric metric, int offset)
			throws IOException {
		double sum = 0.0d;

		if (offset >= this.sampleSize)
			return sum;

		for (int i = offset; i < offset + numOfPivots - 1; i++) {
			for (int j = i + 1; j < offset + numOfPivots; j++) {
				sum += metric.dist(sample[i], sample[j]);
			}
		}
		return sum;
	}

	/**
	 * For each pivot set, we compute the distance between every two pivots; The
	 * pivot set with maximum sum distance is taken as the final pivot set.
	 * 
	 * Note that the selected pivot is set to first set in the sample
	 * 
	 * @param metric
	 * @throws Exception
	 */
	private void randomSelect(IMetric metric) throws IOException {
		int pSetID = 0, i;
		double sum, tmp;

		sum = getSumDistFromPivotSet(metric, 0);
		for (i = 1; i < numOfPivotSets; i++) {
			tmp = getSumDistFromPivotSet(metric, numOfPivots * i);
			if (tmp > sum) {
				sum = tmp;
				pSetID = i;
			}
		}

		if (pSetID != 0) {
			// move the selected pivot set to the first set
			for (i = 0; i < numOfPivots; i++) {
				sample[i] = sample[pSetID * numOfPivots + i];
			}
		}
	}

	/**
	 * 
	 * @param distMatrix
	 *            : distance matrix
	 * @param sumDistToPivots
	 *            : the sum of distance for each remainding object to the pivots
	 * @param maxID
	 *            : the latest selected pivot
	 * @return
	 */
	private int selObjWithMaxDist(float[][] distMatrix,
			float[] sumDistToPivots, int maxID) {
		int selectedID = -1;
		float totSum = 0;

		for (int i = 0; i < sampleSize; i++) {
			if (sumDistToPivots[i] >= 0) {
				sumDistToPivots[i] += distMatrix[i][maxID];
				if (sumDistToPivots[i] > totSum) {
					totSum = sumDistToPivots[i];
					selectedID = i;
				}
			}
		}
		return selectedID;
	}

	private void swap(int src, int tar, int[] permuteMatrix) {
		if (src < 0 || src > sampleSize || tar < 0 || tar > sampleSize) {
			return;
		}

		Object o = sample[src];
		sample[src] = sample[tar];
		sample[tar] = o;

		if (permuteMatrix == null)
			return;

		int tmpID;
		tmpID = permuteMatrix[src];
		permuteMatrix[src] = permuteMatrix[tar];
		permuteMatrix[tar] = tmpID;
	}

	private float[][] compDistMatrix(IMetric metric) throws IOException {
		float[][] distMatrix = new float[sampleSize][sampleSize];
		int i, j;

		for (i = 0; i < sampleSize; i++) {
			for (j = i + 1; j < sampleSize; j++) {
				distMatrix[i][j] = distMatrix[j][i] = metric.dist(sample[i],
						sample[j]);
			}
			if (i % 5000 == 0)
				System.out.println("================" + i);
		}

		return distMatrix;
	}

	/**
	 * (1) We randomly select a pivot (2) The second pivot has the maximum
	 * distance to the first pivot (3) The ith pivot has the maximum distance to
	 * the selected pivot set
	 * 
	 * @param metric
	 * @throws Exception
	 */
	private void furthestSelect(IMetric metric) throws IOException {
		// random select the first one
		int i, maxID;
		float[][] distMatrix;
		float[] sumDistToPivots = new float[sampleSize];
		Object[] pivotSet = new Object[numOfPivots];

		/** set the init state of elements */
		for (i = 0; i < sampleSize; i++) {
			sumDistToPivots[i] = 0;
		}
		/** compute the distance matrix for objects in the samples */
		distMatrix = compDistMatrix(metric);
		Random ran = new Random();
		maxID = ran.nextInt(this.sampleSize);
		sumDistToPivots[maxID] = -1;
		pivotSet[0] = sample[maxID];

		for (i = 1; i < numOfPivots; i++) {
			/**
			 * update sumDistToPivots for each object in the sample by the
			 * latest selected pivot
			 */
			maxID = selObjWithMaxDist(distMatrix, sumDistToPivots, maxID);
			sumDistToPivots[maxID] = -1;
			pivotSet[i] = sample[maxID];
			// System.out.println("finish " + i + " (" + numOfPivots + ")");
		}
		// move pivots in the sample to the first numOfPivots position
		System.arraycopy(pivotSet, 0, sample, 0, numOfPivots);
	}

	private void greedySelectFirstPivot(IMetric metric, double[] minDist)
			throws IOException {
		int i, j;
		double min_cost = Double.MAX_VALUE, tmp_cost;
		int sel_pid = -1;
		double[] tmp_d = new double[sampleSize];

		for (i = 0; i < sampleSize; i++) {
			tmp_cost = 0.0d;
			for (j = 0; j < sampleSize; j++) {
				tmp_d[j] = metric.dist(sample[i], sample[j]);
				tmp_cost += tmp_d[j];
			}
			if (tmp_cost < min_cost) {
				sel_pid = i;
				min_cost = tmp_cost;
				System.arraycopy(tmp_d, 0, minDist, 0, sampleSize);
			}
		}
		// swap
		swap(0, sel_pid, null);
		// swap d
		minDist[sel_pid] = minDist[0];
		minDist[0] = 0;
	}

	/**
	 * 
	 * @param metric
	 *            : distance function
	 * @param minDist
	 *            : distance array in which we maintain the distance from each
	 *            object to its closest pivot. At the beginning, the distance is
	 *            the maximum double value.
	 * @param from
	 */
	private void greedySelectNextPivot(IMetric metric, double[] minDist,
			int from) throws IOException {
		int i, j, sel_id = -1;
		double max_reduced_cost = Double.MAX_VALUE;
		double[] max_reduced_cost_each = new double[sampleSize];
		double[] tmp_reduced_cost_each = new double[sampleSize];

		for (i = from; i < sampleSize; i++) {
			double dist, reduced_cost = 0.0d;

			for (j = from; j < sampleSize; j++) {
				dist = metric.dist(sample[i], sample[j]);
				if (dist < minDist[j]) {
					// yes, we save some thing by adding object sample[i]
					tmp_reduced_cost_each[j] = minDist[j] - dist;
					reduced_cost += tmp_reduced_cost_each[j];
				} else {
					tmp_reduced_cost_each[j] = 0;
				}
			}
			// if saved more, then selects object sample[i] as the candidate
			if (max_reduced_cost > reduced_cost) {
				max_reduced_cost = reduced_cost;
				sel_id = i;
				System.arraycopy(tmp_reduced_cost_each, 0,
						max_reduced_cost_each, 0, sampleSize);
			}
		}
		// update minDist
		for (i = from; i < sampleSize; i++) {
			minDist[i] = minDist[i] - max_reduced_cost_each[i];
		}
		// swap
		swap(from, sel_id, null);
		minDist[sel_id] = minDist[from];
		minDist[from] = 0;
	}

	private void greedySelect(IMetric metric) throws IOException {
		int pid = 1;
		double[] minDist = new double[sampleSize];

		// select the first pivot
		greedySelectFirstPivot(metric, minDist);
		System.out.println("finish 1 (" + numOfPivots + ")");
		// iteratively select the pivots such that the sum of distance is
		// reduced at most
		while (pid < numOfPivots) {
			greedySelectNextPivot(metric, minDist, pid++);
			System.out.println("finish " + pid + " (" + numOfPivots + ")");
		}
	}

	@Override
	public void output(String path) {
		Configuration conf = new Configuration();
	    conf.addResource(new Path(SQConfig.configPath1));
	    conf.addResource(new Path(SQConfig.configPath2));
	    conf.addResource(new Path(SQConfig.configPath3));
	    
		try {
			FileSystem fs = FileSystem.get(conf);
			FSDataOutputStream outputStream = fs.create(new Path(path), true);
			BufferedWriter br = null;
			br = new BufferedWriter(new OutputStreamWriter(outputStream));

			for (int i = 0; i < numOfPivots; i++) {
				// we just add an invalid id for each pivot for the consistency
				String line = i + "," + metricSpace.outputDim(sample[i]);
				br.write(line + "\n");
			}

			br.close();
			outputStream.close();
			fs.close();
		} catch (IOException e) {
			e.printStackTrace();
		}
	}

	/**
	 * select pivots from the data set
	 * 
	 * @param args
	 *            :
	 */
	public static void pivotSelectionMain(String[] args)
			throws ClassNotFoundException, IOException {
		String strDomain;
		String strMetric;
		String inputPath;
		String outputPath;
		String strStrategy;
		int numOfPivots;
		int sampleSize;
		int totSize;
		int dim;

		Configuration conf = new Configuration();
		new GenericOptionsParser(conf, args).getRemainingArgs();
	    conf.addResource(new Path(SQConfig.configPath1));
	    conf.addResource(new Path(SQConfig.configPath2));
	    conf.addResource(new Path(SQConfig.configPath3));
	    
		inputPath = conf.get(SQConfig.dataset) + Path.SEPARATOR + "0.data";
		outputPath = conf.get(SQConfig.strPivotInput);
		strDomain = conf.get(SQConfig.strMetricSpace);
		strMetric = conf.get(SQConfig.strMetric);
		strStrategy = conf.get(SQConfig.strSelStrategy);
		numOfPivots = conf.getInt(SQConfig.strNumOfPivots, -1);
		sampleSize = conf.getInt(SQConfig.strSampleSize, -1);
		totSize = conf.getInt(SQConfig.strDatasetSize, -1);
		dim = conf.getInt(SQConfig.strDimExpression, -1);

		IMetricSpace metricSpace = null;
		IMetric metric = null;

		if (strMetric.compareToIgnoreCase("L1Metric") == 0) {
			metric = new MetricFactory.L1Metric();
		} else if (strMetric.compareToIgnoreCase("L2Metric") == 0) {
			metric = new MetricFactory.L2Metric();
		} else {
			throw new ClassNotFoundException("The input metric " + strMetric
					+ " is not yet supported!");
		}

		if (strDomain.compareTo("vector") == 0) {
			metricSpace = new MetricSpaceFactory.VectorSpace(metric);
		} else {
			throw new ClassNotFoundException("The input metricspace "
					+ strDomain + "is not yet supported!");
		}

		System.err.println("inputPath: " + inputPath + "\noutputPath: "
				+ outputPath + "\nDomain: " + strDomain + ", metric: "
				+ strMetric + ", NumOfPivots: " + numOfPivots
				+ ", NumOfSample: " + sampleSize + ", TotSize: " + totSize);

		// make sure that the sample size is the multiple times to numOfPivots
		if (sampleSize % numOfPivots != 0) {
			throw new IOException(
					"The sizeOfSample must be set to the multiple times of numOfPivots");
		}

		// read, select, and output
		APivotSelection ps = new PivotSelectionFactory(metricSpace,
				numOfPivots, dim);
		ps.read(inputPath, sampleSize, totSize);
		APivotSelection.SelStrategy ss;
		if (strStrategy.compareToIgnoreCase("random") == 0) {
			ss = APivotSelection.SelStrategy.random;
			ps.selectPivots(ss, metric);
		} else if (strStrategy.compareToIgnoreCase("furthest") == 0) {
			ss = APivotSelection.SelStrategy.furthest;
			ps.selectPivots(ss, metric);
		} else {
			throw new ClassNotFoundException("The input selection strategy "
					+ strStrategy + "is not yet supported!");
		}
		ps.output(outputPath);
	}
}
